var DbTableInitializer = require('../DbTableInitializer'),
	fn = require('functions');

var ApplicationsInitializer = module.exports = DbTableInitializer.extend({

	create: function (callback) {
		this.adapter.create({fields: "\
			`id` int(10) unsigned NOT NULL AUTO_INCREMENT,\
			`school` varchar(50) COLLATE utf8_czech_ci NOT NULL,\
			`grade` tinyint(4) NOT NULL,\
			`expires` timestamp NULL DEFAULT NULL,\
			`approved` timestamp NULL DEFAULT NULL,\
			`userId` int(10) unsigned NOT NULL,\
			PRIMARY KEY (`id`)"}, callback);
	}

});