var DirectoryIndexHandlers = require('./DirectoryIndexHandlers'),
	HTTP = require('http-status'),
	fs = require('fs');

var FileStorageHandlers = module.exports = DirectoryIndexHandlers.extend({

// handler methods

	index: null,

	create: function (req, res) {
	},

	load: function (req, id, next) {
		this.fillPath(req, function (err, path, partial) {
			if (err || partial) {
				next(err, null);
			} else {
				path = path + '/' + id;
				fs.exists(path, function (exists) {
					next(null, exists ? path : null);
				});
			}
		});
	},

	show: function (req, res) {
		res.sendfile(this.getItem(req));
	},

	update: function (req, res) {
	},

	destroy: function (req, res) {
	}

});