var HTTP = require('http-status'),
	fn = require('functions');

var loadChecker = module.exports = function (name) {

	return function (req, res, action, next) {
		switch (action) {
			case 'show':
			case 'update':
			case 'destroy':
				if (fn.isObject(req[name])) {
					next();
				} else {
					res.send(HTTP.NOT_FOUND);
				}
				break;
			default:
				next();
		}
	};
};